@mcp.tool()
async def add_documents(
    collection_name: str,
    documents: List[str],
    ids: List[str],
    metadatas: OneOrMany[Metadata] | None = None,
) -> str:
    """ThÃªm tÃ i liá»‡u vÃ o má»™t collection cá»§a Chroma, Ä‘áº£m báº£o thá»±c hiá»‡n embedding náº¿u cáº§n.

    Tham sá»‘:
        collection_name: TÃªn cá»§a collection cáº§n thÃªm tÃ i liá»‡u.
        documents: Danh sÃ¡ch cÃ¡c tÃ i liá»‡u vÄƒn báº£n cáº§n thÃªm.
        ids: Danh sÃ¡ch cÃ¡c ID cho cÃ¡c tÃ i liá»‡u (báº¯t buá»™c).
        metadatas: Danh sÃ¡ch metadata tÃ¹y chá»n cho má»—i tÃ i liá»‡u.
    """
    if not documents:
        raise ValueError("The 'documents' list cannot be empty.")

    if not ids:
        raise ValueError("The 'ids' list is required and cannot be empty.")

    if any(not id.strip() for id in ids):
        raise ValueError("IDs cannot be empty strings.")

    if len(ids) != len(documents):
        raise ValueError(
            f"Number of ids ({len(ids)}) must match number of documents ({len(documents)})."
        )

    client = get_chroma_client()
    try:
        collection = client.get_or_create_collection(collection_name)

        # Check for duplicate IDs
        existing_ids = collection.get(include=[])["ids"]
        duplicate_ids = [id for id in ids if id in existing_ids]
        if duplicate_ids:
            raise ValueError(
                f"The following IDs already exist in collection '{collection_name}': {duplicate_ids}. "
                f"Use 'update_documents' to update existing documents."
            )

        # Check for embedding_function
        use_embedding = getattr(collection, "embedding_function", None)
        if use_embedding:
            # Collection cÃ³ embedding function, Ä‘á»ƒ ChromaDB tá»± Ä‘á»™ng xá»­ lÃ½
            logger.info(
                f"Using collection's built-in embedding function for {len(documents)} documents"
            )
            result = collection.add(documents=documents, metadatas=metadatas, ids=ids)
        else:
            # Manually compute embeddings sá»­ dá»¥ng default embedding function
            logger.info(f"Computing embeddings manually for {len(documents)} documents")
            embedding_fn = mcp_known_embedding_functions["default"]
            embeddings = embedding_fn(documents)
            result = collection.add(
                documents=documents, metadatas=metadatas, ids=ids, embeddings=embeddings
            )

        # Process result
        if result and isinstance(result, dict):
            if "success" in result and not result["success"]:
                raise Exception(
                    f"Failed to add documents: {result.get('error', 'Unknown error')}"
                )
            if "count" in result:
                return f"Successfully added {result['count']} documents to collection {collection_name}"

        return f"Successfully added {len(documents)} documents to collection {collection_name}, result is {result}"
    except Exception as e:
        raise Exception(
            f"Failed to add documents to collection '{collection_name}': {str(e)}"
        ) from e


@mcp.tool()
async def query_documents(
    collection_name: str,
    query_texts: List[str],
    n_results: int = 5,
    where: Dict | None = None,
    where_document: Dict | None = None,
    include: List[str] = ["documents", "metadatas", "distances"],
) -> Dict:
    """Truy váº¥n tÃ i liá»‡u tá»« má»™t collection cá»§a Chroma vá»›i cÃ¡c bá»™ lá»c nÃ¢ng cao.

    Tham sá»‘:
        collection_name: TÃªn cá»§a collection cáº§n truy váº¥n.
        query_texts: Danh sÃ¡ch cÃ¡c vÄƒn báº£n truy váº¥n Ä‘á»ƒ tÃ¬m kiáº¿m.
        n_results: Sá»‘ lÆ°á»£ng káº¿t quáº£ cáº§n tráº£ vá» cho má»—i truy váº¥n.
        where: Bá»™ lá»c metadata tÃ¹y chá»n sá»­ dá»¥ng cÃ¡c toÃ¡n tá»­ truy váº¥n cá»§a Chroma.
               VÃ­ dá»¥:
               - So sÃ¡nh Ä‘Æ¡n giáº£n: {"metadata_field": "value"}
               - So sÃ¡nh: {"metadata_field": {"$gt": 5}}
               - AND logic: {"$and": [{"field1": {"$eq": "value1"}}, {"field2": {"$gt": 5}}]}
               - OR logic: {"$or": [{"field1": {"$eq": "value1"}}, {"field1": {"$eq": "value2"}}]}
        where_document: Bá»™ lá»c ná»™i dung tÃ i liá»‡u tÃ¹y chá»n.
        include: Danh sÃ¡ch cÃ¡c thÃ´ng tin cáº§n bao gá»“m trong káº¿t quáº£. Máº·c Ä‘á»‹nh bao gá»“m tÃ i liá»‡u, metadata vÃ  khoáº£ng cÃ¡ch.
    """
    if not query_texts:
        raise ValueError("The 'query_texts' list cannot be empty.")

    client = get_chroma_client()
    try:
        collection = client.get_collection(collection_name)
        results = collection.query(
            query_texts=query_texts,
            n_results=n_results,
            where=where,
            where_document=where_document,
            include=include,  # type: ignore
        )
        return dict(results)  # Convert QueryResult to dict
    except Exception as e:
        raise Exception(
            f"Failed to query documents from collection '{collection_name}': {str(e)}"
        ) from e


